    private final Pattern phonePattern = Pattern.compile("(\\+[1-9]{1}[0-9]{1,2}+|00[1-9]{1}[0-9]{1,2}+)[\\(\\)\\.\\-\\s\\d]{6,16}");

    private static FormatsUtil instance = null;

    private FormatsUtil() {
    }

    public static FormatsUtil getInstance() {

        if (instance == null) {
            instance = new FormatsUtil();
        }

        return instance;
    }

    public String validateBitcoinAddress(final String address) {

        if (isValidBitcoinAddress(address)) {
            return address;
        } else {
            String addr = uri2BitcoinAddress(address);
            if (addr != null) {
                return addr;
            } else {
                return null;
            }
        }
    }

    public boolean isBitcoinUri(final String s) {

        boolean ret;

        try {
            new BitcoinURI(s);
            ret = true;
        } catch (BitcoinURIParseException bupe) {
            ret = false;
        }

        return ret;
    }

    public String getBitcoinUri(final String s) {

        String ret;
        BitcoinURI uri;

        try {
            uri = new BitcoinURI(s);
            ret = uri.toString();
        } catch (BitcoinURIParseException bupe) {
            ret = null;
        }

        return ret;
    }

    public String getBitcoinAddress(final String s) {

        String ret;
        BitcoinURI uri;

        try {
            uri = new BitcoinURI(s);
            Address address = uri.getAddress();
            assert address != null;
            ret = address.toString();
        } catch (BitcoinURIParseException bupe) {
            ret = null;
        }

        return ret;
    }

    public String getBitcoinAmount(final String s) {

        String ret;
        BitcoinURI uri;

        try {
            uri = new BitcoinURI(s);
            if (uri.getAmount() != null) {
                ret = uri.getAmount().toString();
            } else {
                ret = "0.0000";
            }
        } catch (BitcoinURIParseException bupe) {
            ret = null;
        }

        return ret;
    }

    public boolean isValidBitcoinAddress(final String address) {

        boolean ret;

        try {
            new Address(MainNetParams.get(), address);
            ret = true;
        } catch (WrongNetworkException wne) {
            ret = false;
        } catch (AddressFormatException afe) {
            ret = false;
        }

        return ret;
    }

    public boolean isValidEmailAddress(final String address) {
        return emailPattern.matcher(address).matches();
    }

    public boolean isValidMobileNumber(final String mobile) {
        return phonePattern.matcher(mobile).matches();
    }

    private String uri2BitcoinAddress(final String address) {

        String ret;
        BitcoinURI uri;

        try {
            uri = new BitcoinURI(address);
            Address address1 = uri.getAddress();
            assert address1 != null;
            ret = address1.toString();
        } catch (BitcoinURIParseException bupe) {
            ret = null;
        }

        return ret;
    }

    public boolean isValidXpub(String xpub) {

        try {
            byte[] xpubBytes = Base58.decodeChecked(xpub);

            ByteBuffer byteBuffer = ByteBuffer.wrap(xpubBytes);
            if (byteBuffer.getInt() != 0x0488B21E) {
                throw new AddressFormatException("invalid version: " + xpub);
            } else {

                byte[] chain = new byte[32];
                byte[] pub = new byte[33];
                // depth:
                byteBuffer.get();
                // parent fingerprint:
                byteBuffer.getInt();
                // child no.
                byteBuffer.getInt();
                byteBuffer.get(chain);
                byteBuffer.get(pub);

                ByteBuffer pubBytes = ByteBuffer.wrap(pub);
                int firstByte = pubBytes.get();
                if (firstByte == 0x02 || firstByte == 0x03) {
                    return true;
                } else {
                    throw new AddressFormatException("invalid format: " + xpub);
                }
            }
        } catch (Exception e) {
            return false;
        }
    }

    public boolean isValidJson(String json) {
        try {
            new JSONObject(json);
        } catch (JSONException ex) {
            try {
                new JSONArray(json);
            } catch (JSONException ex1) {
                return false;
            }
        }
        return true;
    }
